<template>
  <header class="emfe-header" :class="headerName">
    <div class="emfe-header-eye">
      <img class="emfe-header-eye-img" :src="logo" height="100%" @click="goHome">
    </div>
    <div class="emfe-header-controller">
      <span class="emfe-header-controller-text">专业的活动应用平台</span>
    </div>
    <div class="emfe-header-info">
      <div class="emfe-header-index">
        <!-- <span class="emfe-header-info-text" @click="goIndex">控制台首页</span> -->
        <emfe-button theme="primary" @click="goIndex">免费发活动</emfe-button>
      </div>
      <div class="emfe-header-account">
        <span class="emfe-header-info-hello">&nbsp;你好，<span class="emfe-header-info-text" @click="goAccount">{{ user }}</span></span>
      </div>
      <span class="emfe-header-info-text emfe-header-signout" @click="logout">退出</span>
    </div>
    <div v-if="isMask" class="emfe-header-mask">
      <div class="emfe-header-mask-box">
        <img class="emfe-header-mask-box-close" @click="closeMask" src="data:image/svg+xml;base64,">
        <p class="emfe-header-mask-box-title">选择发布的活动类型</p>
        <div class="emfe-header-mask-box-modal">
          <div @click="lightFun" class="emfe-header-mask-box-modal-light">
            <img class="emfe-header-mask-box-modal-light-img" :src="lightImg">
            <p class="emfe-header-mask-box-modal-light-title">报名活动</p>
            <p class="emfe-header-mask-box-modal-light-content">俱乐部、户外、会议沙龙、亲子等活动</p>
            <div class="emfe-header-mask-box-modal-light-mask"></div>
          </div>
          <div @click="eventeFun" class="emfe-header-mask-box-modal-light">
            <img class="emfe-header-mask-box-modal-light-img" :src="eventImg">
            <p class="emfe-header-mask-box-modal-light-title">演出赛事</p>
            <p class="emfe-header-mask-box-modal-light-content">专业的票务活动、演唱会、剧院、音乐节、赛事等</p>
            <div class="emfe-header-mask-box-modal-light-mask"></div>
          </div>
        </div>
      </div>
    </div>
  </header>
</template>
<script>
export default {
  name: 'EmfeHeader',
  data() {
    return {
      lightImg: 'data:image/svg+xml;base64,',
      eventImg: 'data:image/svg+xml;base64,',
      isMask: false,
    };
  },
  props: {
    logo: {
      type: String,
      default: 'https://static2.evente.cn/static/img/logo2017.svg',
    },
    user: {
      type: String,
      required: true,
    },
    className: String,
    home: Function,
    showMask: {
      type: Boolean,
      default: true,
    },
  },
  computed: {
    headerName() {
      return [
        {
          [`${this.className}-header`]: !!this.className,
        },
      ];
    },
  },
  methods: {
    logout() {
      this.$emit('logout');
    },
    goIndex() {
      if (this.showMask) {
        this.isMask = true;
      } else {
        this.$emit('goIndex');
      }
    },
    closeMask() {
      this.isMask = false;
    },
    goAccount() {
      this.$emit('goAccount');
    },
    eventeFun() {
      this.$emit('goEvente');
    },
    lightFun() {
      this.$emit('goLight');
    },
    goHome() {
      if (this.home) {
        this.home();
      } else {
        window.open('https://www.evente.cn/');
        this.$emit('goHome');
      }
    },
  },
};
</script>
